import React, { Component } from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import Paper from '@material-ui/core/Paper';
import {withStyles} from '@material-ui/core/styles';


const styles = theme => ({
  paper: {
    marginTop: theme.spacing.unit * 8,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: `${theme.spacing.unit * 2}px ${theme.spacing.unit * 3}px ${theme.spacing.unit * 3}px`,
  }
});



class App extends Component {
  constructor(){
    super();
    var url = "http://localhost:8080"
    this.state={
      textInput:'',
    }
  }
  handle(event){
    this.setState({
      textInput:event.target.value
    })
  }

  fetchData = (type,message) => {
    var typeMessage;
    if(type === "decode"){
      typeMessage = "decodeMessage";
    }else if (type === "encode") {
      typeMessage = "encodeMessage";
    }else {
      console.log("wrong type");
      return 0;
    }
    fetch(url+"/api/fence/"+type, {
      method: "POST",
      dataType: "JSON",
      body: {
	      typeMessage : message
      },
      headers: {
        "Content-Type": "application/json; charset=utf-8",
      }
    })
    .then((resp) => {
      console.log(resp.json());
      return resp.json();
    })
    .catch((error) =>{
      console.log("Coś się zjebało");
    })
  }

  render() {
    return (
      <div className="App">
        <header className="App-header main">
        <Paper className="paper">
            <FormControl margin="normal">
              <InputLabel htmlFor="text">Text</InputLabel>
              <Input name="text" id="text" ref={(value) => this.textInput = value} onChange={this.handle.bind(this)}/>
            </FormControl>
            <div>
              <Button variant="contained" color="primary" onClick={this.fetchData("encode",this.textInput)}>
                <p>encode</p>
              </Button>
              <Button variant="contained" color="primary" onClick={this.fetchData("decode",this.textInput)}>
                <p>decode</p>
              </Button>
            </div>
            <Input name="output" autoComplete="No output" />
          </Paper>
        </header>
      </div>
    );
  }
}

export default withStyles(styles)(App) ;
