import React, { Component } from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import Paper from '@material-ui/core/Paper';
import withStyles from '@materia-ui/core/styles';


const styles = theme => ({
  paper: {
    marginTop: theme.spacing.unit * 8,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: `${theme.spacing.unit * 2}px ${theme.spacing.unit * 3}px ${theme.spacing.unit * 3}px`,
  }
});



class App extends Component {

  fetchData = () => {
    fetch("......./suggestions/random", {
      method: "GET",
      dataType: "JSON",
      headers: {
        "Content-Type": "application/json; charset=utf-8",
      }
    })
    .then((resp) => {
      return resp.json()
    })
    .then((data) => {
      this.setState({ suggestion: data.suggestion })
    })
    .catch((error) => {
      console.log(error, "catch the hoop")
    })
  }


  render() {
    return (
      <div className="App">
        <header className="App-header main">
        <Paper className="paper">
            <FormControl margin="normal">
              <InputLabel htmlFor="password">Text</InputLabel>
              <Input name="password" type="password" id="password" autoComplete="current-password" />
            </FormControl>
            <div>
              <Button variant="contained" color="primary" onClick={this.fetchData()}>
                <p>encode</p>
              </Button>
              <Button variant="contained" color="primary" onClick={this.fetchData()}>
                <p>decode</p>
              </Button>
            </div>
            <Input name="output" autoComplete="No output" />
          </Paper>
        </header>
      </div>
    );
  }
}

export default withStyles(styles)(App) ;
