import React, { Component } from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import Paper from '@material-ui/core/Paper';
import {withStyles} from '@material-ui/core/styles';



class App extends Component {
  fetchData = (type,message) => {
    var typeMessage = type+"Message";
    var body = {
      method: "POST",
      dataType: "JSON",
      body: {
	      typeMessage : message
      },
      headers: {
        "Content-Type": "application/json; charset=utf-8",
      }
    }
    console.log(body)
    fetch("http://localhost:8080/api/fence/"+type,body)
    .then((resp) => {
      console.log(resp.json());
      return resp.json();
    })
  }

  render() {
    return (
      <div className="App">
        <header className="App-header main">
        <Paper className="paper">
            <FormControl margin="normal">
              <InputLabel htmlFor="text">Text</InputLabel>
              <Input name="text" id="text" ref={(value) => this.textInput = value} onChange={this.handle.bind(this)}/>
            </FormControl>
            <div>
              <Button variant="contained" color="primary" onClick={this.fetchData("encode",this.textInput.state)}>
                <p>encode</p>
              </Button>
              <Button variant="contained" color="primary" onClick={this.fetchData("decode",this.textInput.state)}>
                <p>decode</p>
              </Button>
            </div>
            <Input name="output" autoComplete="No output" />
          </Paper>
        </header>
      </div>
    );
  }
}

export default App ;
