import React, { Component } from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import FormControl from '@material-ui/core/FormControl';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import TextField from '@material-ui/core/TextField'
import Paper from '@material-ui/core/Paper';
import {withStyles} from '@material-ui/core/styles';



class App extends Component {
  fetchData = (type,message) => {
    var typeMessage = type+"Message";
    var body = {
      method: "POST",
      dataType: "JSON",
      body: {
	      typeMessage : message
      },
      headers: {
        "Content-Type": "application/json; charset=utf-8",
      }
    }
    console.log(body)
    fetch("http://localhost:8080/api/fence/"+type,body)
    .then((resp) => {
      console.log(resp.json());
      return resp.json();
    })
  }

  render() {
    return (
      <div className="App">
        <header className="App-header main">
          <Paper>
            <FormControl margin="normal">
              <InputLabel htmlFor="text">Enter text</InputLabel>
              <Input ref="message" id="text"></Input>
            </FormControl>
            <div>
              <Button variant="contained" color="primary" >encode</Button>
              <Button variant="contained" color="primary" >decode</Button>
            </div>
            <TextField disabled="true" id="output"></TextField>
          </Paper>
        </header>
      </div>
    );
  }
}

export default App ;
